{
  "description": "Notify Draco of all context changes for MySQL on port 5050",
  "subject": {
    "entities": [
      {
        "idPattern": ".*"
      }
    ]
  },
  "notification": {
    "http": {
      "url": "http://draco:5050/notify"
    },
    "attrsFormat": "legacy"
  },
  "throttling": 5
}
